import React, { Component } from 'react';
import logo from '../logo.svg';
import '../stylesheets/Header.css';

class Header extends Component {

  navTo(e,uri){
    window.location.href = window.location.origin + uri;
    document.getElementsByClassName('App-header').getElementsByTagName("H2").removeClass('active');
    console.log(e);
    // this.className('active');
  }

  render() {
    return (
      <div className="App-header">
        <h1 onClick={() => {this.navTo(e,'')}}>Udacitrivia</h1>
        <h2 onClick={() => {this.navTo(e,'')}}>List</h2>
        <h2 onClick={() => {this.navTo(e,'/add')}}>Add</h2>
        <h2 onClick={() => {this.navTo(e,'/play')}}>Play</h2>
      </div>
    );
  }
}

export default Header;
